input CreateProductInput {
  name: String!
}

extend type Mutation {
  createProduct(input: CreateProductInput!): ProductPayload!
}

input CreateSubProductInput {
  productId: String!
  name: String!
}

extend type Mutation {
  createSubProduct(input: CreateSubProductInput!): SubProductPayload!
}

input ProductInput {
  id: String!
}

type ProductPayload {
  id: String!
  name: String!
}

extend type Query {
  product(input: ProductInput!): ProductPayload
}

type SubProductPayload {
  id: String!
  name: String!
}

extend type ProductPayload {
  subProducts: [SubProductPayload!]!
}
