input CreateProductInput {
  name: String!
}

extend type Mutation {
  createProduct(input: CreateProductInput!): Product!
}

input CreateSubProductInput {
  productId: String!
  name: String!
}

extend type Mutation {
  createSubProduct(input: CreateSubProductInput!): SubProduct!
}

input ProductInput {
  id: String!
}

type SubProduct {
  id: String!
  name: String!
}

type Product {
  id: String!
  name: String!
  subProducts: [SubProduct!]!
}

extend type Query {
  product(input: ProductInput!): Product
}
